mapper.select:
SELECT pn.notice_no, pn.title, pn.perfect_score, pn.deadline, pn.deadline_add
FROM professor_notice pn
WHERE pn.noitice_no = #{notice_no}

mapper.update:
UPDATE professor_notice
SET title = #{title}
content = #{content}
perfect_score = #{perfect_score}
submitdate = date_format(now(), '%Y-%m-%d')
deadline = #{deadline}
deadline_add = #{deadline_add}
WHERE notice_no = #{notice_no}
